import React, { useState, useEffect } from 'react';

interface CategoryPlayProps {
  prompts: string[];
  players: string[];
  roundsToPlay: number;
  timePerRound: number;
  isTeamMode?: boolean;
  team1?: string[];
  team2?: string[];
  onGameComplete: (scores: { [playerName: string]: number }, teamScores?: { team1: number; team2: number }) => void;
  onBack: () => void;
}

const CategoryPlay: React.FC<CategoryPlayProps> = ({
  prompts,
  players,
  roundsToPlay,
  timePerRound,
  isTeamMode = false,
  team1 = [],
  team2 = [],
  onGameComplete,
  onBack
}) => {
  const [currentRound, setCurrentRound] = useState(1);
  const [currentPrompt, setCurrentPrompt] = useState('');
  const [timeLeft, setTimeLeft] = useState(timePerRound);
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [usedPrompts, setUsedPrompts] = useState<string[]>([]);
  const [scores, setScores] = useState<{ [playerName: string]: number }>(() => {
    const initialScores: { [playerName: string]: number } = {};
    players.forEach(player => {
      initialScores[player] = 0;
    });
    return initialScores;
  });
  const [teamScores, setTeamScores] = useState({ team1: 0, team2: 0 });
  const [showVoting, setShowVoting] = useState(false);

  // Select a random prompt when component mounts or when moving to next round
  useEffect(() => {
    selectRandomPrompt();
  }, [currentRound]);

  // Timer countdown
  useEffect(() => {
    if (isTimerRunning && timeLeft > 0) {
      const timer = setTimeout(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0 && isTimerRunning) {
      setIsTimerRunning(false);
      setShowVoting(true);
    }
  }, [isTimerRunning, timeLeft]);

  const selectRandomPrompt = () => {
    const availablePrompts = prompts.filter(p => !usedPrompts.includes(p));
    if (availablePrompts.length === 0) {
      // Reset if all prompts used
      setUsedPrompts([]);
      const randomPrompt = prompts[Math.floor(Math.random() * prompts.length)];
      setCurrentPrompt(randomPrompt);
      setUsedPrompts([randomPrompt]);
    } else {
      const randomPrompt = availablePrompts[Math.floor(Math.random() * availablePrompts.length)];
      setCurrentPrompt(randomPrompt);
      setUsedPrompts([...usedPrompts, randomPrompt]);
    }
  };

  const startTimer = () => {
    setIsTimerRunning(true);
  };

  const handleVote = (playerName: string) => {
    // Award point to voted player
    setScores({
      ...scores,
      [playerName]: scores[playerName] + 1
    });

    // Move to next round or end game
    if (currentRound >= roundsToPlay) {
      onGameComplete(scores);
    } else {
      setCurrentRound(currentRound + 1);
      setTimeLeft(timePerRound);
      setIsTimerRunning(false);
      setShowVoting(false);
    }
  };

  const skipVoting = () => {
    // No points awarded, just move to next round
    if (currentRound >= roundsToPlay) {
      onGameComplete(scores);
    } else {
      setCurrentRound(currentRound + 1);
      setTimeLeft(timePerRound);
      setIsTimerRunning(false);
      setShowVoting(false);
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (showVoting) {
    return (
      <div className="min-h-screen bg-dark-purple px-6 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8 mt-12">
          <button onClick={onBack} className="text-white text-2xl">
            ←
          </button>
          <div className="text-white font-semibold">
            Round {currentRound} / {roundsToPlay}
          </div>
        </div>

        {/* Voting Screen */}
        <div className="max-w-md mx-auto">
          <div className="text-center mb-8">
            <h2 className="text-3xl font-bold text-white mb-4">Vote for the Best!</h2>
            <p className="text-gray-300">Who had the funniest answer?</p>
          </div>

          {/* Player Voting Cards */}
          <div className="space-y-3 mb-6">
            {players.map((playerName, index) => (
              <button
                key={index}
                onClick={() => handleVote(playerName)}
                className="w-full bg-card-bg rounded-2xl p-5 border border-purple-glow hover:shadow-card-glow transition-all duration-300 transform hover:scale-105"
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 bg-gradient-to-br from-pink-600 to-purple-600 rounded-full flex items-center justify-center">
                      <span className="text-white text-lg font-bold">
                        {playerName.charAt(0).toUpperCase()}
                      </span>
                    </div>
                    <div className="text-left">
                      <h3 className="text-white font-semibold text-lg">{playerName}</h3>
                      <p className="text-gray-400 text-sm">{scores[playerName]} points</p>
                    </div>
                  </div>
                  <div className="text-white text-2xl">👍</div>
                </div>
              </button>
            ))}
          </div>

          {/* Skip Button */}
          <button
            onClick={skipVoting}
            className="w-full bg-gray-600 rounded-2xl py-3 text-white font-semibold hover:bg-gray-700 transition-colors"
          >
            Skip / No Winner
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-dark-purple px-6 py-8 flex flex-col">
      {/* Header */}
      <div className="flex items-center justify-between mb-8 mt-12">
        <button onClick={onBack} className="text-white text-2xl">
          ←
        </button>
        <div className="text-white font-semibold">
          Round {currentRound} / {roundsToPlay}
        </div>
      </div>

      {/* Timer Circle */}
      <div className="flex justify-center mb-8">
        <div className="relative">
          <div className={`w-32 h-32 rounded-full flex items-center justify-center ${
            timeLeft <= 10 ? 'bg-red-600' : 'bg-gradient-to-br from-pink-600 to-purple-600'
          } shadow-purple-glow`}>
            <span className={`text-4xl font-bold text-white ${timeLeft <= 10 ? 'animate-pulse' : ''}`}>
              {formatTime(timeLeft)}
            </span>
          </div>
        </div>
      </div>

      {/* Prompt Card */}
      <div className="flex-1 flex items-center justify-center mb-8">
        <div className="w-full max-w-md">
          <div className="bg-gradient-to-br from-pink-600 to-purple-600 rounded-3xl p-8 shadow-2xl">
            <div className="text-center">
              <span className="text-6xl mb-6 block">🎯</span>
              <h2 className="text-2xl font-bold text-white leading-relaxed">
                {currentPrompt}
              </h2>
            </div>
          </div>
        </div>
      </div>

      {/* Scoreboard */}
      <div className="bg-card-bg rounded-2xl p-4 mb-6">
        <h3 className="text-white font-semibold mb-3 text-center">Current Scores</h3>
        <div className="grid grid-cols-3 gap-2">
          {players.map((playerName, index) => (
            <div key={index} className="text-center">
              <div className="w-10 h-10 bg-gradient-to-br from-pink-600 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-1">
                <span className="text-white text-sm font-bold">
                  {playerName.charAt(0).toUpperCase()}
                </span>
              </div>
              <p className="text-white text-xs truncate">{playerName}</p>
              <p className="text-purple-300 text-sm font-bold">{scores[playerName]}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Start/Vote Button */}
      {!isTimerRunning ? (
        <button
          onClick={startTimer}
          className="w-full bg-gradient-to-r from-pink-600 to-purple-600 rounded-2xl py-4 text-white font-bold text-xl shadow-lg hover:shadow-purple-glow transition-all duration-300 transform hover:scale-105"
        >
          Start Round!
        </button>
      ) : (
        <button
          onClick={() => setShowVoting(true)}
          className="w-full bg-gradient-to-r from-green-600 to-emerald-600 rounded-2xl py-4 text-white font-bold text-xl shadow-lg hover:shadow-purple-glow transition-all duration-300 transform hover:scale-105"
        >
          Vote Now!
        </button>
      )}
    </div>
  );
};

export default CategoryPlay;
