import React from 'react';

interface PlayerOverviewProps {
  players: Array<{
    id: string;
    name: string;
    isImposter?: boolean;
    votes?: number;
  }>;
  onPlayerClick: (playerIndex: number) => void;
  onBack: () => void;
}

const PlayerOverview: React.FC<PlayerOverviewProps> = ({
  players,
  onPlayerClick,
  onBack,
}) => {
  return (
    <div className="min-h-screen bg-dark-purple flex flex-col items-center justify-center p-6">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-white text-3xl font-bold mb-2">Players Ready</h1>
        <p className="text-gray-300 text-lg">Tap on a player to reveal their word</p>
      </div>

      {/* Players List */}
      <div className="w-full max-w-md space-y-4 mb-8">
        {players.map((player, index) => (
          <div
            key={player.id}
            className="bg-card-bg rounded-2xl p-4 border border-purple-glow hover:shadow-card-glow transition-all duration-300"
          >
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-gradient-to-br from-accent-purple to-light-purple rounded-full flex items-center justify-center">
                <span className="text-white text-lg font-bold">
                  {player.name.charAt(0).toUpperCase()}
                </span>
              </div>
              <div className="flex-1">
                <h3 className="text-white font-semibold text-lg">{player.name}</h3>
                <p className="text-gray-400 text-sm">Player {index + 1}</p>
              </div>
              <div className="text-white text-2xl">üë§</div>
            </div>
          </div>
        ))}
      </div>

      {/* Tap to Continue */}
      <div 
        className="w-full max-w-md bg-gradient-to-r from-accent-purple to-light-purple rounded-2xl p-8 text-center cursor-pointer hover:shadow-purple-glow transition-all duration-300 transform hover:scale-105"
        onClick={onStartGame}
      >
        <div className="text-white text-xl font-bold mb-2">Tap to Start</div>
        <div className="text-purple-200 text-sm">Begin word reveal</div>
      </div>

      {/* Back Button */}
      <button
        onClick={onBack}
        className="mt-6 text-gray-400 hover:text-white transition-colors duration-300"
      >
        ‚Üê Back to Setup
      </button>
    </div>
  );
};

export default PlayerOverview;
