import React, { useState, useEffect } from 'react';
import { Player, GameConfig } from '../App';

interface PlayerSelectionProps {
  gameConfig: GameConfig;
  players: Player[];
  onPlayersChange: (players: Player[]) => void;
  onStartGame: () => void;
  onPlayerClick: (playerIndex: number) => void;
  onBack: () => void;
  currentPlayerIndex: number;
}

const PlayerSelection: React.FC<PlayerSelectionProps> = ({
  gameConfig,
  players,
  onPlayersChange,
  onStartGame,
  onPlayerClick,
  onBack,
  currentPlayerIndex,
}) => {
  const [playerName, setPlayerName] = useState('');
  const [localPlayerIndex, setLocalPlayerIndex] = useState(0);

  // Initialize players if empty
  useEffect(() => {
    if (players.length === 0) {
      const initialPlayers: Player[] = [];
      for (let i = 0; i < gameConfig.playerCount; i++) {
        initialPlayers.push({
          id: `player-${i}`,
          name: '',
          isImposter: false,
          votes: 0,
        });
      }
      onPlayersChange(initialPlayers);
    }
  }, [gameConfig.playerCount, players.length, onPlayersChange]);

  const handleNameSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (playerName.trim()) {
      const updatedPlayers = [...players];
      updatedPlayers[localPlayerIndex].name = playerName.trim();
      onPlayersChange(updatedPlayers);
      setPlayerName('');

      if (localPlayerIndex < players.length - 1) {
        setLocalPlayerIndex(localPlayerIndex + 1);
      }
    }
  };

  const handlePlayerClick = (index: number) => {
    if (players[index].name) {
      // Set the current player index and start the game
      onPlayerClick(index);
    }
  };

  const allPlayersNamed = players.every(player => player.name.trim() !== '');
  const canStartGame = allPlayersNamed && players.length === gameConfig.playerCount;

  return (
    <div className="min-h-screen bg-dark-purple px-6 py-8">
      {/* Status Bar */}
      <div className="absolute top-0 left-0 right-0 flex justify-between items-center px-6 py-2 text-sm">
        <span>2:51</span>
        <div className="flex items-center space-x-2">
          <div className="w-4 h-4 bg-white rounded-full opacity-80"></div>
          <div className="w-4 h-4 bg-white rounded-full opacity-60"></div>
          <div className="w-4 h-4 bg-white rounded-full opacity-40"></div>
          <div className="w-4 h-4 bg-white rounded-full opacity-20"></div>
          <div className="w-6 h-3 bg-white rounded-sm opacity-80"></div>
          <div className="w-8 h-4 bg-yellow-400 rounded-md flex items-center justify-center">
            <span className="text-xs text-black font-bold">94</span>
          </div>
        </div>
      </div>

      {/* Header */}
      <div className="flex items-center justify-between mb-8 mt-12">
        <button onClick={onBack} className="text-white text-2xl">
          ←
        </button>
        <h1 className="text-white text-2xl font-bold">Players</h1>
        <div></div>
      </div>

      {/* Instructions */}
      <div className="text-center mb-8">
        <p className="text-white text-lg leading-relaxed">
          {allPlayersNamed 
            ? "All players ready! Tap 'Start Game' to begin."
            : "Tap your name to reveal your word, then pass the device to the next player."
          }
        </p>
      </div>

      {/* Player Name Input */}
      {!allPlayersNamed && (
        <div className="mb-8">
          <form onSubmit={handleNameSubmit} className="text-center">
          <div className="mb-4">
            <label className="text-white text-lg font-medium block mb-2">
              Player {localPlayerIndex + 1}, enter your name:
            </label>
              <input
                type="text"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                className="w-full max-w-xs bg-card-bg border border-purple-glow rounded-xl px-4 py-3 text-white text-center text-lg focus:outline-none focus:ring-2 focus:ring-purple-glow"
                placeholder="Your name"
                autoFocus
              />
            </div>
            <button
              type="submit"
              disabled={!playerName.trim()}
              className="bg-gradient-to-r from-accent-purple to-light-purple rounded-xl px-8 py-3 text-white font-semibold disabled:opacity-50 disabled:cursor-not-allowed hover:shadow-purple-glow transition-all duration-300"
            >
              Add Player
            </button>
          </form>
        </div>
      )}

      {/* Player Grid */}
      <div className="grid grid-cols-2 gap-4 mb-8">
        {players.map((player, index) => (
          <button
            key={player.id}
            onClick={() => handlePlayerClick(index)}
            className={`relative bg-card-bg rounded-2xl p-6 border transition-all duration-300 ${
              player.name 
                ? 'border-purple-glow hover:shadow-card-glow cursor-pointer' 
                : 'border-gray-600 opacity-60'
            }`}
          >
            {/* Avatar */}
            <div className="flex flex-col items-center text-center">
              <div className="w-16 h-16 bg-gradient-to-br from-accent-purple to-light-purple rounded-full flex items-center justify-center mb-3">
                <span className="text-white text-2xl font-bold">
                  {player.name ? player.name.charAt(0).toUpperCase() : '?'}
                </span>
              </div>
              <span className="text-white text-lg font-medium">
                {player.name || `Player ${index + 1}`}
              </span>
              {!player.name && (
                <span className="text-gray-400 text-sm mt-1">
                  Tap to add name
                </span>
              )}
            </div>
          </button>
        ))}
      </div>

      {/* Start Game Button */}
      {canStartGame && (
        <button
          onClick={onStartGame}
          className="w-full bg-gradient-to-r from-accent-purple to-light-purple rounded-2xl py-4 text-white font-bold text-xl shadow-lg hover:shadow-purple-glow transition-all duration-300 transform hover:scale-105"
        >
          Start Game
        </button>
      )}

      {/* Progress Indicator */}
      <div className="mt-6 text-center">
        <div className="flex justify-center space-x-2 mb-2">
          {players.map((_, index) => (
            <div
              key={index}
              className={`w-2 h-2 rounded-full ${
                players[index].name ? 'bg-purple-glow' : 'bg-gray-600'
              }`}
            ></div>
          ))}
        </div>
        <p className="text-gray-400 text-sm">
          {players.filter(p => p.name).length} of {players.length} players ready
        </p>
      </div>
    </div>
  );
};

export default PlayerSelection;
