import React, { useState, useEffect } from 'react';
import { Player, GameConfig } from '../App';

interface WordRevealProps {
  players: Player[];
  gameConfig: GameConfig;
  currentPlayerIndex: number;
  onNextPlayer: (index: number) => void;
  onBack: () => void;
}

// Indian-themed words organized by category
const WORDS_BY_CATEGORY: { [key: string]: string[] } = {
  'Bollywood Movies': ['Dilwale Dulhania Le Jayenge', 'Sholay', 'Lagaan', '3 Idiots', 'Dangal', 'PK'],
  'Indian Food': ['Biryani', 'Samosa', 'Butter Chicken', 'Masala Dosa', 'Rajma Chawal', 'Chole Bhature'],
  'Cricket': ['Sachin Tendulkar', 'Virat Kohli', 'MS Dhoni', 'IPL', 'Test Match', 'Sixer'],
  'Indian Cities': ['Mumbai', 'Delhi', 'Bangalore', 'Chennai', 'Kolkata', 'Pune'],
  'Festivals': ['Diwali', 'Holi', 'Dussehra', 'Eid', 'Christmas', 'Ganesh Chaturthi'],
  'Indian Sweets': ['Gulab Jamun', 'Jalebi', 'Rasgulla', 'Laddu', 'Barfi', 'Kheer'],
  'Bollywood Actors': ['Amitabh Bachchan', 'Shah Rukh Khan', 'Aamir Khan', 'Salman Khan', 'Deepika Padukone', 'Priyanka Chopra'],
  'Indian States': ['Maharashtra', 'Tamil Nadu', 'Gujarat', 'Rajasthan', 'Kerala', 'Punjab'],
  'Street Food': ['Vada Pav', 'Pani Puri', 'Chaat', 'Bhel Puri', 'Dahi Puri', 'Aloo Tikki'],
  'Indian Traditions': ['Namaste', 'Tilak', 'Mehendi', 'Sindoor', 'Mangalsutra', 'Karva Chauth'],
  'Hindi Songs': ['Mere Sapno Ki Rani', 'Chaiyya Chaiyya', 'Tum Hi Ho', 'Gerua', 'Ae Watan', 'Despacito Hindi'],
  'Indian Monuments': ['Taj Mahal', 'Red Fort', 'Qutub Minar', 'Gateway of India', 'Hawa Mahal', 'Charminar'],
  'Indian Games': ['Kabaddi', 'Gilli Danda', 'Kho Kho', 'Cricket', 'Chess', 'Carrom'],
  'Indian Clothing': ['Sari', 'Kurta', 'Sherwani', 'Lehenga', 'Dhoti', 'Salwar Kameez'],
  'Indian Brands': ['Tata', 'Reliance', 'Mahindra', 'Infosys', 'Wipro', 'TCS'],
  'Indian TV Shows': ['Ramayan', 'Mahabharat', 'Taarak Mehta', 'Bhabhi Ji Ghar Par', 'Kumkum Bhagya', 'Yeh Rishta'],
  'Indian Mythology': ['Ram', 'Krishna', 'Ganesha', 'Durga', 'Hanuman', 'Shiva'],
  'Indian Languages': ['Hindi', 'Tamil', 'Telugu', 'Bengali', 'Marathi', 'Gujarati'],
  'Indian Dance Forms': ['Bharatanatyam', 'Kathak', 'Odissi', 'Bhangra', 'Garba', 'Kuchipudi'],
  'Indian Spices': ['Turmeric', 'Cumin', 'Coriander', 'Cardamom', 'Cinnamon', 'Cloves']
};

const WordReveal: React.FC<WordRevealProps> = ({
  players,
  gameConfig,
  currentPlayerIndex,
  onNextPlayer,
  onBack,
}) => {
  const [currentPlayer, setCurrentPlayer] = useState<Player | null>(null);
  const [, setSecretWord] = useState('');
  const [, setCategory] = useState('');
  const [showWord, setShowWord] = useState(false);
  const [gameStarted, setGameStarted] = useState(false);

  useEffect(() => {
    if (!gameStarted && players.length > 0) {
      // Get words from selected categories only
      const selectedCategories = gameConfig.selectedCategories || [
        'Bollywood Movies', 'Indian Food', 'Cricket', 'Indian Cities', 'Festivals'
      ];

      // Collect all words from selected categories
      const availableWords: string[] = [];
      selectedCategories.forEach(category => {
        if (WORDS_BY_CATEGORY[category]) {
          availableWords.push(...WORDS_BY_CATEGORY[category]);
        }
      });

      // Shuffle and select a word
      const shuffledWords = [...availableWords].sort(() => Math.random() - 0.5);
      const selectedWord = shuffledWords[0];

      // Select a random category from the selected ones
      const shuffledCategories = [...selectedCategories].sort(() => Math.random() - 0.5);
      const selectedCategory = shuffledCategories[0];

      setSecretWord(selectedWord);
      setCategory(selectedCategory);

      // Assign imposters randomly
      const shuffledPlayers = [...players].sort(() => Math.random() - 0.5);
      players.forEach(player => {
        const isImposter = shuffledPlayers.slice(0, gameConfig.imposterCount).some(p => p.id === player.id);
        player.isImposter = isImposter;
        player.word = isImposter ? (gameConfig.showCategoryToImposter ? selectedCategory : 'IMPOSTER') : selectedWord;
      });

      // Update players in parent component
      setGameStarted(true);
    }
  }, [players, gameConfig, gameStarted]);

  useEffect(() => {
    if (players.length > 0 && currentPlayerIndex < players.length) {
      setCurrentPlayer(players[currentPlayerIndex]);
    }
  }, [players, currentPlayerIndex]);

  const handleRevealWord = () => {
    setShowWord(true);
  };

  const handleNext = () => {
    setShowWord(false); // Reset reveal state for next player
    onNextPlayer(currentPlayerIndex + 1);
  };

  if (!currentPlayer) {
    return (
      <div className="min-h-screen bg-dark-purple flex items-center justify-center">
        <div className="text-white text-xl">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-dark-purple flex flex-col">
      {/* Status Bar */}
      <div className="absolute top-0 left-0 right-0 flex justify-between items-center px-6 py-2 text-sm">
        <span>2:51</span>
        <div className="flex items-center space-x-2">
          <div className="w-4 h-4 bg-white rounded-full opacity-80"></div>
          <div className="w-4 h-4 bg-white rounded-full opacity-60"></div>
          <div className="w-4 h-4 bg-white rounded-full opacity-40"></div>
          <div className="w-4 h-4 bg-white rounded-full opacity-20"></div>
          <div className="w-6 h-3 bg-white rounded-sm opacity-80"></div>
          <div className="w-8 h-4 bg-yellow-400 rounded-md flex items-center justify-center">
            <span className="text-xs text-black font-bold">94</span>
          </div>
        </div>
      </div>

      {/* Header */}
      <div className="flex items-center justify-between px-6 py-4 mt-12">
        <button onClick={onBack} className="text-white text-2xl">
          ←
        </button>
        <div></div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col items-center justify-center px-6">
        {/* Player Name */}
        <div className="text-center mb-8">
          <p className="text-gray-300 text-lg mb-2">The word for</p>
          <p className="text-light-purple text-2xl font-bold">{currentPlayer.name}</p>
        </div>

        {/* Word Display */}
        {!showWord ? (
          <div className="w-full max-w-sm mb-8">
            <button
              onClick={handleRevealWord}
              className="w-full bg-black bg-opacity-50 rounded-3xl p-8 text-center relative hover:bg-opacity-70 transition-all duration-300"
            >
              {/* Dotted pattern overlay */}
              <div className="absolute inset-0 rounded-3xl overflow-hidden">
                <div className="absolute inset-0" style={{
                  backgroundImage: `radial-gradient(circle at 25% 25%, rgba(168, 85, 247, 0.3) 2px, transparent 2px),
                                  radial-gradient(circle at 75% 25%, rgba(139, 92, 246, 0.3) 2px, transparent 2px),
                                  radial-gradient(circle at 25% 75%, rgba(124, 58, 237, 0.3) 2px, transparent 2px),
                                  radial-gradient(circle at 75% 75%, rgba(168, 85, 247, 0.3) 2px, transparent 2px)`,
                  backgroundSize: '20px 20px'
                }}></div>
              </div>
              <div className="relative z-10">
                <p className="text-light-blue text-lg mb-4">Tap the box to reveal</p>
                <div className="w-8 h-8 mx-auto text-light-blue">👆</div>
              </div>
            </button>
          </div>
        ) : (
          <div className="w-full max-w-sm mb-8">
            {currentPlayer.isImposter ? (
              <div className="bg-gradient-to-br from-red-600 to-red-700 rounded-3xl p-8 text-center border-4 border-red-500 shadow-red-500 shadow-lg">
                <div className="text-red-200 text-lg font-bold mb-2">Imposter</div>
                <div className="text-red-100 text-sm">You don't know the word!</div>
              </div>
            ) : (
              <div className="bg-card-bg rounded-3xl p-8 text-center border border-purple-glow shadow-card-glow">
                <div className="text-cyan-400 text-2xl font-bold">{currentPlayer.word}</div>
              </div>
            )}

            {/* Hint for Imposter */}
            {currentPlayer.isImposter && gameConfig.showHintToImposter && (
              <div className="bg-card-bg rounded-2xl p-6 mt-6 border border-purple-glow">
                <div className="flex items-center space-x-3 mb-3">
                  <span className="text-yellow-400 text-xl">💡</span>
                  <span className="text-white font-medium">Your Clue</span>
                </div>
                <div className="text-white text-xl font-bold mb-2">BOUQUET</div>
                <div className="text-gray-300 text-sm">Use this in the first round to blend in!</div>
              </div>
            )}
          </div>
        )}

        {/* Action Button */}
        <div className="w-full max-w-sm">
          {!showWord ? (
            <button
              onClick={handleRevealWord}
              className="w-full bg-gradient-to-r from-accent-purple to-light-purple rounded-2xl py-4 text-white font-bold text-lg shadow-lg hover:shadow-purple-glow transition-all duration-300 transform hover:scale-105"
            >
              Got it!
            </button>
          ) : (
            <button
              onClick={handleNext}
              className="w-full bg-gradient-to-r from-accent-purple to-light-purple rounded-2xl py-4 text-white font-bold text-lg shadow-lg hover:shadow-purple-glow transition-all duration-300 transform hover:scale-105"
            >
              {currentPlayerIndex < players.length - 1 ? 'Next Player' : 'Start Voting'}
            </button>
          )}
        </div>

        {/* Progress Indicator */}
        <div className="mt-8 text-center">
          <div className="flex justify-center space-x-2 mb-2">
            {players.map((_, index) => (
              <div
                key={index}
                className={`w-2 h-2 rounded-full ${
                  index < currentPlayerIndex ? 'bg-purple-glow' : 
                  index === currentPlayerIndex ? 'bg-white' : 'bg-gray-600'
                }`}
              ></div>
            ))}
          </div>
          <p className="text-gray-400 text-sm">
            Player {currentPlayerIndex + 1} of {players.length}
          </p>
        </div>
      </div>

      {/* Home Indicator */}
      <div className="w-32 h-1 bg-white bg-opacity-30 rounded-full mb-4 mx-auto"></div>
    </div>
  );
};

export default WordReveal;
