import React, { useState, useEffect } from 'react';
import MainMenu from './components/MainMenu';
import GameSetup from './components/GameSetup';
import WordReveal from './components/WordReveal';
import VotingScreen from './components/VotingScreen';
import ResultsScreen from './components/ResultsScreen';
import CategoryGameSetup from './components/CategoryGameSetup';

export type GameState = 
  | 'main-menu'
  | 'game-setup'
  | 'word-reveal'
  | 'voting'
  | 'results'
  | 'category-setup'
  | 'category-play'
  | 'category-voting'
  | 'category-results';

export interface Player {
  id: string;
  name: string;
  isImposter: boolean;
  word?: string;
  votes: number;
}

export interface GameConfig {
  playerCount: number;
  imposterCount: number;
  showCategoryToImposter: boolean;
  showHintToImposter: boolean;
  selectedCategories?: string[];
  playerNames?: string[];
}

function App() {
  const [gameState, setGameState] = useState<GameState>('main-menu');
  const [players, setPlayers] = useState<Player[]>([]);

  // Load game config from localStorage or use defaults
  const [gameConfig, setGameConfig] = useState<GameConfig>(() => {
    const saved = localStorage.getItem('imposter-game-config');
    if (saved) {
      try {
        return JSON.parse(saved);
      } catch (e) {
        console.error('Error parsing saved game config:', e);
      }
    }
    return {
      playerCount: 3,
      imposterCount: 1,
      showCategoryToImposter: false,
      showHintToImposter: true,
    };
  });

  const [currentPlayerIndex, setCurrentPlayerIndex] = useState(0);
  const [, setSecretWord] = useState('');
  const [gameResults, setGameResults] = useState<{
    winner: 'crew' | 'imposter';
    imposterRevealed: boolean;
    imposters: Player[];
  } | null>(null);

  // Save game config to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('imposter-game-config', JSON.stringify(gameConfig));
  }, [gameConfig]);

  const resetGame = () => {
    setGameState('main-menu');
    setPlayers([]);
    setCurrentPlayerIndex(0);
    setSecretWord('');
    setGameResults(null);
  };

  const initializePlayersFromConfig = () => {
    if (gameConfig.playerNames) {
      const initialPlayers: Player[] = gameConfig.playerNames.map((name, index) => ({
        id: `player-${index}`,
        name: name,
        isImposter: false,
        votes: 0,
      }));
      setPlayers(initialPlayers);
    }
  };

  const startImposterGame = () => {
    setGameState('game-setup');
  };

  const startCategoryGame = () => {
    setGameState('category-setup');
  };

  const renderCurrentScreen = () => {
    switch (gameState) {
      case 'main-menu':
        return (
          <MainMenu
            onStartImposter={startImposterGame}
            onStartCategory={startCategoryGame}
          />
        );
      case 'game-setup':
        return (
          <GameSetup
            gameConfig={gameConfig}
            onConfigChange={setGameConfig}
            onStartGame={() => {
              // Initialize players from config and go directly to word reveal
              initializePlayersFromConfig();
              setGameState('word-reveal');
            }}
            onBack={() => setGameState('main-menu')}
          />
        );
      case 'word-reveal':
        return (
          <WordReveal
            key={players.length > 0 ? players[0].id : 'word-reveal'}
            players={players}
            gameConfig={gameConfig}
            currentPlayerIndex={currentPlayerIndex}
            onNextPlayer={(index) => {
              if (index >= players.length) {
                setGameState('voting');
              } else {
                setCurrentPlayerIndex(index);
              }
            }}
            onBack={() => {
              // Reset game state when going back
              setPlayers([]);
              setCurrentPlayerIndex(0);
              setGameState('game-setup');
            }}
          />
        );
      case 'voting':
        return (
          <VotingScreen
            players={players}
            onVoteComplete={(results) => {
              setGameResults(results);
              setGameState('results');
            }}
            onBack={() => setGameState('word-reveal')}
          />
        );
      case 'results':
        return (
          <ResultsScreen
            results={gameResults!}
            players={players}
            onPlayAgain={resetGame}
            onBackToMenu={resetGame}
          />
        );
      case 'category-setup':
        return (
          <CategoryGameSetup
            onStartGame={(config) => {
              // TODO: Initialize category game with config
              alert('Category game play screen coming next!');
              console.log('Category game config:', config);
            }}
            onBack={() => setGameState('main-menu')}
          />
        );
      default:
        return <MainMenu onStartImposter={startImposterGame} onStartCategory={startCategoryGame} />;
    }
  };

  return (
    <div className="min-h-screen bg-dark-purple text-white">
      {renderCurrentScreen()}
    </div>
  );
}

export default App;
